INSERT INTO `craft_entryversions` (`id`, `entryId`, `sectionId`, `creatorId`, `locale`, `num`, `notes`, `data`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('148', '430', '10', '1', 'en_us', '1', NULL, '{\"authorId\":\"1\",\"title\":\"Blyth Love of Truth\",\"slug\":\"blyth-love-of-truth\",\"postDate\":1402005670,\"expiryDate\":null,\"enabled\":1,\"fields\":{\"44\":\"R. H. Blyth\",\"2\":\"<p>The whole is the whole, but the half is infinite. For this reason Lessing said that if there were held out to him in one hand truth, and in the other the love of truth, and he might choose freely between the two, he would prefer the latter to the former.<\\/p>\"}}', '2014-06-05 22:01:10', '2014-06-05 22:01:10', 'c9912cea-3761-4d63-8b3e-37be3e6cfce1');
