INSERT INTO `craft_entryversions` (`id`, `entryId`, `sectionId`, `creatorId`, `locale`, `num`, `notes`, `data`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('220', '434', '9', '1', 'en_us', '3', '', '{\"authorId\":\"1\",\"title\":\"Designing For People\",\"slug\":\"designing-for-people\",\"postDate\":1402020960,\"expiryDate\":null,\"enabled\":1,\"fields\":{\"42\":\"Henry Dreyfuss\",\"2\":\"<p>For a sixty year old book on a fast-changing thing, <em>Designing for People <\\/em>holds up extremely well.<\\/p>\",\"43\":[\"433\"],\"32\":\"\",\"38\":{\"435\":{\"type\":\"readingQuote\",\"enabled\":\"1\",\"fields\":{\"readingQuotation\":\"<p>[A designer] operates on the theory that it is better to be right than to be original; therefore, he steers a course somewhere between daring and caution.<\\/p>\",\"readingPage\":\"24\",\"readingNote\":\"\"}}},\"37\":\"\"}}', '2014-06-22 01:58:07', '2014-06-22 01:58:07', '216f45d3-35a8-4d8b-8c9b-7cd685d6e8ad');
INSERT INTO `craft_entryversions` (`id`, `entryId`, `sectionId`, `creatorId`, `locale`, `num`, `notes`, `data`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('221', '434', '9', '1', 'en_us', '4', '', '{\"authorId\":\"1\",\"title\":\"Designing For People\",\"slug\":\"designing-for-people\",\"postDate\":1402020960,\"expiryDate\":null,\"enabled\":1,\"fields\":{\"42\":\"Henry Dreyfuss\",\"2\":\"<p>For a sixty year old book on a fast-changing thing, <em>Designing for People <\\/em>holds up extremely well.<\\/p>\",\"43\":[\"433\"],\"32\":\"\",\"38\":{\"new1\":{\"type\":\"readingQuote\",\"enabled\":\"1\",\"fields\":{\"readingQuotation\":\"<p>An honest job of design should flow from the inside out, not from the outside in.<\\/p>\",\"readingPage\":\"18\",\"readingNote\":\"\"}},\"new2\":{\"type\":\"readingQuote\",\"enabled\":\"1\",\"fields\":{\"readingQuotation\":\"<p>The industrial designer began by eliminating excess decoration, but his real job began when he insisted on dissecting the product, seeing what made it tick, and devising means of making it tick better -- then making it look better. He never forgets that beauty is only skin-deep. For years in our office we have kept before us the concept that <em>what we are working on is going to be ridden in, sat upon, looked at, talked into, activated, operated, or in some way used by people individually or en masse. If the point of contact between the product and the people becomes a point of friction, then the industrial designer has failed. If , on the other hand, people are made safer, more comfortable, more eager to purchase, more efficient -- or just plain happier -- the designer has succeeded.<\\/em><\\/p>\",\"readingPage\":\"24\",\"readingNote\":\"\"}},\"435\":{\"type\":\"readingQuote\",\"enabled\":\"1\",\"fields\":{\"readingQuotation\":\"<p>[A designer] operates on the theory that it is better to be right than to be original; therefore, he steers a course somewhere between daring and caution.<\\/p>\",\"readingPage\":\"24\",\"readingNote\":\"\"}}},\"37\":\"\"}}', '2014-06-22 02:11:45', '2014-06-22 02:11:45', 'ef819466-2cb4-44b5-850c-bcab5bdb003a');
